package com.discursive.cjcook.guava.collect;

import java.util.Set;

import com.google.common.collect.Sets;

public class SetUnionExample {
  public static void main(String[] args) {
    Set<String> names1 = Sets.newHashSet();
    names1.add( "Tim" );
    names1.add( "Tom" );
    names1.add( "Ted" );

    Set<String> names2 = Sets.newHashSet();
    names2.add( "Susan" );
    names2.add( "Tony" );
    names2.add( "Ted" );

    Set<String> union = Sets.union( names1, names2 );
    for( String name : union ) {
      System.out.printf( "%s\n", name);
    }
  }
}

package com.discursive.cjcook.guava.collect;

import java.util.Set;

import com.google.common.collect.Sets;

public class SetIntersectionExample {
  public static void main(String[] args) {
    Set<String> names1 = Sets.newHashSet();
    names1.add("Tim");
    names1.add("Tom");
    names1.add("Ted");

    Set<String> names2 = Sets.newHashSet();
    names2.add("Susan");
    names2.add("Tony");
    names2.add("Ted");

    Set<String> intersection = Sets.intersection(names1, names2);
    for (String name : intersection) {
      System.out.printf("%s\n", name);
    }
  }
}

package com.discursive.cjcook.guava.collect;

import java.util.Set;

import com.google.common.collect.Sets;

public class SetDifferenceExample {
  public static void main(String[] args) {
    Set<String> names1 = Sets.newHashSet();
    names1.add("Tim");
    names1.add("Tom");
    names1.add("Ted");

    Set<String> names2 = Sets.newHashSet();
    names2.add("Susan");
    names2.add("Tony");
    names2.add("Ted");

    Set<String> difference = Sets.difference(names1, names2);
    for (String name : difference) {
      System.out.printf("%s\n", name);
    }
  }
}

package com.discursive.cjcook.guava.collect;

import java.util.Set;

import com.google.common.collect.Sets;

public class SetSymDiffExample {
  public static void main(String[] args) {
    Set<String> names1 = Sets.newHashSet();
    names1.add("Tim");
    names1.add("Tom");
    names1.add("Ted");

    Set<String> names2 = Sets.newHashSet();
    names2.add("Susan");
    names2.add("Tony");
    names2.add("Ted");

    Set<String> symDiff = Sets.symmetricDifference(names1, names2);
    for (String name : symDiff) {
      System.out.printf("%s\n", name);
    }
  }
}

package com.discursive.cjcook.guava.collect;

import java.util.Set;

import com.google.common.collect.ImmutableSet;
import com.google.common.collect.Sets;

public class PowerSetExample {
  public static void main(String[] args) {
    Set<Integer> integers = ImmutableSet.of(1, 2, 3, 4, 5);

    Set<Set<Integer>> powerSet = Sets.powerSet( integers );
    for( Set<Integer> set : powerSet ) {
      System.out.printf( "Powerset: %s\n", set );
    }
  }
}
