example.programlisting:
public class BufferListener implements Runnable {
    private Buffer buffer;

    public BufferListener(Buffer buffer) {
        this.buffer = buffer;
    }

    public void run( ) {
        while(true) {
            String message = (String) buffer.remove( );
            System.out.println( message );
        }
    }
}

